export class UserProfile extends Component {

    state = {

    }

    componentDidMount(){
        axios.get("http://localhost:4001/api/v1/users")
        .then(res => {
            const userCurrent = res.data
            this.setState({
                userLogged: userCurrent
            })
        })


    }
    render() {
        return (
            <Segment>
                <Grid columns={2} relaxed='very'>
