import React, { Component } from 'react'
import { Menu } from 'semantic-ui-react'
import SignUp from './SignUp'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  NavLink
} from "react-router-dom";
import { Button, Header, Image, Modal } from 'semantic-ui-react'

export default class MenuBar extends Component {

  state = {
    showSignUp: false,
    open: false,
    activeItemLE: 'Local Experts',
    activeItemHome: 'home',
    // openModal: false,
    username: "",
    password: "",
    isLogInActive: false
  }




  show = (dimmer) => () => this.setState({ dimmer, open: true })
  close = () => this.setState({ open: false })

  handleOrderClick = (e, { name }) => this.setState({ activeItem: name })


  onChangeHandler = (event) => {
      this.setState({
          [event.target.name]: event.target.value
      })
  }

  logInHandler = (event) => {
    event.preventDefault()
    console.log(this.state)
    // fetch(`http://localhost:3000/users`)
    //   .then(res => res.json())
    //   .then(allUsers => {
    //     // console.log(allUsers)
    //   this.setState =  allUsers.find(function(user) {return user.username && user.password == this.state.username && this.state.password} )
    // })
  }



  handleSignUp = () => {

    this.setState({
      showSignUp: !this.state.showSignUp
    })
  }
  renderSignUp = () => <SignUp />

  handleClick = (e) => {
    console.log(e.target.innerText);
    this.setState({
      activeItem: e.target.innerText
    }, () => {console.log('state activeitem set to: ', this.state.activeItem)})
  }


  render() {
    const { activeItemHome, activeItemLE, open, dimmer, username, password } = this.state

    return (

        <div>
          <Menu>
            <Menu.Item header
              onClick={this.handleClick}
              >
              <NavLink to="/">Local Experts</NavLink>
            </Menu.Item>
            <Menu.Item header
              onClick={this.handleClick}
            >
              <NavLink to="/experiences" >Experiences</NavLink>
            </Menu.Item>
            <Menu.Item>
              <a href="/checkout">Check Out</a>
            </Menu.Item>
            {
              localStorage.user ?
                <>
                  <Menu.Item>
                    {/* onClick={this.renderSignUp}> */}
                    <a href="/signup" >Sign Up</a>
                  </Menu.Item>
                  <Menu.Item>
                  <Button onClick={this.show('blurring')}>LogIn</Button>
                    <Modal dimmer={dimmer} open={open} onClose={this.close}>
                        <Modal.Header>Please Log In Here</Modal.Header>
                        <Modal.Content image>
                          <Image
                            wrapped
                            size='medium'

                          />
                          <Modal.Description>
                            <Header>Enter Log-In</Header>
                            <form onSubmit= {this.logInHandler}>
                                <ul>
                                    <li>
                                        <label htmlFor="username"> Username:</label>
                                        <input type="text"
                                              name="username"
                                              placeholder="Type username here"
                                              value={username}
                                              onChange= {this.onChangeHandler} 
                                                />
                                    </li>

                                    <li>
                                        <label htmlFor="password"> Password:</label>
                                        <input type="text"
                                              name="password" 
                                              placeholder="Type password here"
                                              value={password}
                                              onChange= {this.onChangeHandler} 
                                                />
                                    </li>
                                    <li>
                                        <button type="submit"> Submit </button>
                                    </li>
                                </ul>
                            </form>

                          </Modal.Description>
                        </Modal.Content>
                      </Modal>
                  </Menu.Item>
                </>
              :
                <>
                  <Menu.Item>
                    {/* onClick={this.renderSignUp}> */}
                    <a href="/profile" >Your Profile</a>
                  </Menu.Item>
                  <Menu.Item>
                    {/* onClick={this.renderSignUp}> */}
                    <a href="/logout" >Log-Out</a>
                  </Menu.Item>
                </>
            }
          </Menu>

          {/* {this.state.showSignUp ? */}
            {/* <Router> */}
              {/* <Route path="/signup" exact render={this.renderSignUp} /> */}
              <Route path="/checkout" exact render={() => <></>} />


        </div>

    )
  }
}

